// import { useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useState } from 'react';

const Header = () => {
  return (
    <nav>
      <ul>
        <li>
          <Link to="/">
            <i className="fas fa-home"></i>
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export const SignIn = () => {
  const userErrors = {
    required: 'Username is required to login',
    minLength: 'Username must be at least 3 characters long',
    maxLength: 'Username must be 10 characters long maximum',
    pattern: 'Username must contain only letters and numbers',
  };

  const {
    register,
    handleSubmit,

    formState: { errors },
  } = useForm();

  const [userError, setUserError] = useState(null);
  const [passError, setPassError] = useState(null);

  const onSubmit = (data) => {
    console.log(data);
    setUserError(errors.username ? errors.username.type : null);
    setPassError(errors.password ? errors.password.type : null);
  };
  const onErrors = (errors) => {
    setUserError(errors.username ? errors.username.type : null);
    setPassError(errors.password ? errors.password.type : null);
  };

  return (
    <div className="centerScreen">
      <Header />
      <div className="loginContainer">
        <form onSubmit={handleSubmit(onSubmit, onErrors)}>
          <label htmlFor="username">Userame</label>
          <input
            id="username"
            type="text"
            placeholder="Enter username"
            aria-invalid={errors.username ? 'true' : 'false'}
            {...register('username', {
              required: true,
              minLength: 3,
              maxLength: 10,
              pattern: /^[a-z0-9]+$/i,
            })}
          />

          {userError && <span role="alert">{userErrors[userError]}</span>}

          <input type="submit" />
        </form>
        {/* <form>
          <label for="username">Username</label>
          <input
            type="text"
            placeholder="Enter username"
            name="username"
            required
          />

          <label for="pwd">Password</label>
          <input
            type="password"
            placeholder="Enter password"
            name="pwd"
            required
          />

          <button>Login</button>
        </form> */}
        <div className="swapLogin">
          <p>
            Don't have an account? <Link to="/register">Sign up here!</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export const SignUp = () => {
  return (
    <div className="centerScreen">
      <Header />
      <div className="loginContainer">
        <form>
          <label for="username">Username</label>
          <input
            type="text"
            placeholder="Enter username"
            name="username"
            required
          />

          <label for="email">Email</label>
          <input type="text" placeholder="Enter email" name="email" required />

          <label for="pwd">Password</label>
          <input
            type="password"
            placeholder="Enter password"
            name="pwd"
            required
          />

          <button>Login</button>
        </form>
        <div className="swapLogin">
          <p>
            Already have an account? <Link to="/login">Sign in here!</Link>
          </p>
        </div>
      </div>
    </div>
  );
};
