// import { useEffect, useReducer, useState } from 'react';
// import { Header } from './Header';
// import { useParams } from 'react-router-dom';
import { Header } from './Header';

const Reply = () => {
  return (
    <div className="reply">
      <h5>replier</h5>
      <p>Body of a reply</p>
    </div>
  );
};

const Replies = () => {
  return (
    <div className="replies">
      <Reply />
    </div>
  );
};

const CommentBox = () => {
  return (
    <div className="commentBox">
      <textarea rows="1" cols="1" name="text"></textarea>
    </div>
  );
};

const Comment = () => {
  return (
    <div className="comment">
      <h5>commentor</h5>
      <p>Body of a comment</p>
      <button>Reply</button>
      <CommentBox />
      <Replies />
    </div>
  );
};

const Comments = () => {
  return (
    <div className="comments">
      <Comment />
    </div>
  );
};

const Content = () => {
  return (
    <div className="content">
      <p>Likes</p>
      <h3>This is my post</h3>
      <h5>coudei</h5>
      <p>This is a random post</p>
    </div>
  );
};

export const Post = () => {
  return (
    <>
      <Header />

      <Content />

      <CommentBox />

      <Comments />
    </>
  );
};
