import React, { useEffect, useReducer, useState } from 'react';
import './App.css';
import { Routes, Route } from 'react-router-dom';

const Header = () => {
  return (
    <header>
      <nav>
        <ul>
          <li>
            <a href="#!">
              <i className="fas fa-home"></i>
            </a>
          </li>
          <li>
            <a href="#!">Login</a>
          </li>
        </ul>
      </nav>
    </header>
  );
};

const Post = ({ author, title, commentCount, likes }) => {
  return (
    <div className="post">
      <div className="postContents">
        <h3 className="postTitle">
          {author}: {title}
        </h3>
        <p className="postComments">{commentCount} comments</p>
      </div>
      <p className="postLikes">
        <i className="far fa-thumbs-up"> {likes}</i>
      </p>
    </div>
  );
};

const Posts = ({ pageNum }) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/be/getPosts/${pageNum}`)
      .then((response) => response.json())
      .then((posts) => {
        if (!posts.err) {
          setData((d) => {
            if (d) {
              return [...d, ...posts];
            } else {
              return posts;
            }
          });
        }
      });
  }, [pageNum]);

  if (!data) {
    return <h1>Loading...</h1>;
  } else {
    console.log(pageNum);
    return data.map(({ _id, author, title, commentCount, likes }) => (
      <Post
        key={_id}
        author={author}
        title={title}
        commentCount={commentCount}
        likes={likes}
      />
    ));
  }
};

const Page = () => {
  const [currPage, setCurrPage] = useState(1);
  const [loading, toggleLoading] = useReducer((loading) => !loading, false);

  const nextPage = () => {
    setCurrPage(currPage + 1);
  };

  const handleScroll = () => {
    if (
      window.innerHeight + window.pageYOffset >=
        document.body.offsetHeight - 2 &&
      !loading
    ) {
      toggleLoading();
      nextPage();
      console.log('bottom');
      setTimeout(toggleLoading, 500);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  });

  return (
    <div className="posts">
      <Posts pageNum={currPage} />
    </div>
  );
};

const Feed = () => {
  return (
    <>
      <Header />
      <Page />
    </>
  );
};

const App = () => {
  return (
    <div>
      <Routes>
        <Route path="/" element={<Feed />} />
      </Routes>
    </div>
  );
};

export default App;
