import { useEffect, useReducer, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Header } from './Header';
import { dateToString } from './postDate';
import { LikeButton } from './Like';
import Alert from './alert';

const Reply = ({ reply }) => {
  const { author, body, date } = reply;

  const time = dateToString(date);

  return (
    <div className="reply">
      <div className="authorTime">
        <h5>{author}</h5>
        <p className="time">{time}</p>
      </div>

      <p className="bodyText">{body}</p>
    </div>
  );
};

const Replies = ({ replies }) => {
  replies = replies.reverse();
  return (
    <div className="replies">
      {replies.map((reply) => {
        return <Reply key={reply._id} reply={reply} />;
      })}
    </div>
  );
};

const Comment = ({ postId, commentId, comment }) => {
  const { author, body, replies, date } = comment;
  const time = dateToString(date);

  const [hideCommentBox, toggleHideCommentBox] = useReducer(
    (hideCommentBox) => {
      return !hideCommentBox;
    },
    true
  );

  const handleClick = () => {
    toggleHideCommentBox();
  };

  return (
    <div className="comment">
      <div className="authorTime">
        <h5>{author}</h5>
        <p className="time">{time}</p>
      </div>
      <p className="bodyText">{body}</p>
      <button className="submitButton" onClick={handleClick}>
        Reply
      </button>
      <div
        className="comment"
        style={{ display: hideCommentBox ? 'none' : 'block' }}
      >
        <textarea
          className="commentBox"
          aria-label="Reply Box"
          rows="1"
          cols="1"
          name="Reply Box"
          defaultValue="Type reply here..."
          maxLength={500}
        ></textarea>
        <button className="replyButton">Submit Reply</button>
      </div>
      <Replies replies={replies} />
    </div>
  );
};

const Comments = ({ id, comments }) => {
  comments = comments.reverse();
  return (
    <div className="comments">
      {comments.map((comment) => {
        return (
          <Comment
            key={comment._id}
            postId={id}
            commentId={comment._id}
            comment={comment}
          />
        );
      })}
    </div>
  );
};

const Content = ({ postId, likes, usersLiked, author, body, title, date }) => {
  const time = dateToString(date);
  return (
    <div className="content">
      <LikeButton totalLikes={likes} usersLiked={usersLiked} postId={postId} />
      <div className="contentPost">
        <h3>{title}</h3>
        <div className="authorTime">
          <h5>{author}</h5>
          <p className="time">{time}</p>
        </div>
        <p className="bodyText">{body}</p>
      </div>
    </div>
  );
};

export const Post = () => {
  const { id } = useParams();

  const [data, setData] = useState(null);
  const [commentText, setText] = useState('Type comment here...');

  useEffect(() => {
    fetch(`http://localhost:3001/be/getPost/${id}`)
      .then((response) => response.json())
      .then(setData);
  }, [id]);

  const handleInput = ({ target }) => {
    target.style.height = '';
    target.style.height = target.scrollHeight + 10 + 'px';
    setText(target.value);
  };

  const handleFocus = ({ target }) => {
    if (target.value === 'Type comment here...') {
      target.value = '';
    }
  };

  const handleBlur = ({ target }) => {
    if (target.value === '') {
      target.value = 'Type comment here...';
    }
  };

  const handleClick = ({ target }) => {
    const user = localStorage.getItem('name');
    const alertModal = document.getElementById('alertModal');
    const alertText = document.getElementById('alertText');

    if (!user) {
      alertText.innerHTML = 'You need to login to submit comments!';
      alertModal.style.display = 'block';
    } else if (commentText === 'Type comment here...') {
      alertText.innerHTML = 'You need to enter text to post!';
      alertModal.style.display = 'block';
    }
    target.blur();
  };

  if (!data) {
    return (
      <>
        <Header />
        <div className="loader"></div>
      </>
    );
  } else {
    const { _id, likes, usersLiked, author, body, title, date, comments } =
      data;
    return (
      <>
        <Header scroll={true} />
        <div className="postContainer">
          <Content
            postId={_id}
            likes={likes}
            usersLiked={usersLiked}
            author={author}
            body={body}
            title={title}
            date={date}
          />
          <div className="comment">
            <textarea
              className="commentBox"
              aria-label="Comment Box"
              rows="1"
              cols="1"
              name="Comment Box"
              value={commentText}
              maxLength="500"
              onInput={handleInput}
              onFocus={handleFocus}
              onBlur={handleBlur}
            ></textarea>
            <button className="commentBtn" onClick={handleClick}>
              <svg
                width="100px"
                height="40px"
                viewBox="0 0 100 40"
                className="border"
              >
                <polyline
                  points="99,1 99,39 1,39 1,1 99,1"
                  className="bg-line"
                />
                <polyline
                  points="99,1 99,39 1,39 1,1 99,1"
                  className="hl-line"
                />
              </svg>
              <span>Submit</span>
            </button>
          </div>
          <Comments id={_id} comments={comments} />
        </div>
        <Alert />
      </>
    );
  }
};
