import React, { useEffect, useState } from 'react';
import './App.css';

const Header = () => {
  return (
    <header>
      <nav>
        <ul>
          <li>
            <a href="#!">
              <i className="fas fa-home"></i>
            </a>
          </li>
          <li>
            <a href="#!">Login</a>
          </li>
        </ul>
      </nav>
    </header>
  );
};

const Post = ({ author, title, commentCount, likes }) => {
  return (
    <div className="post">
      <div className="postContents">
        <h3 className="postTitle">
          {author}: {title}
        </h3>
        <p className="postComments">{commentCount} comments</p>
      </div>
      <p className="postLikes">
        <i className="far fa-thumbs-up"> {likes}</i>
      </p>
    </div>
  );
};

const Page = ({ pageNum }) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/be/getPosts/${pageNum}`)
      .then((response) => response.json())
      .then(setData);
  }, [pageNum]);

  if (!data) {
    return <h1>Loading...</h1>;
  } else {
    return data.map(({ _id, author, title, commentCount, likes }) => (
      <Post
        key={_id}
        author={author}
        title={title}
        commentCount={commentCount}
        likes={likes}
      />
    ));
  }
};

const Posts = () => {
  return (
    <div className="posts">
      <Post author="coudei" title="First post" commentCount="2" likes="4" />
    </div>
  );
};

const App = () => {
  return (
    <>
      <Header />
      <Posts />
      <Page pageNum="2" />
    </>
  );
};

export default App;
