import { useEffect, useState } from 'react';

export const LikeButton = ({ totalLikes, usersLiked, postId }) => {
  const [likes, setLikes] = useState(totalLikes);
  const [liked, setLiked] = useState(false);
  const [users, setUsers] = useState(usersLiked);
  const user = localStorage.getItem('name');

  const handleClick = () => {
    if (user) {
      if (liked) {
        setLikes(likes - 1);
        setUsers(users.filter((name) => name !== user));
      } else {
        setLikes(likes + 1);
        setUsers((oldUsers) => [...oldUsers, user]);
      }
      // send new likes count to server
      setLiked(!liked);
      console.log(users);
    } else {
      // alert user to sign in to like a post
      console.log('not logged in');
    }
  };

  useEffect(() => {
    if (!users) {
      setUsers([]);
    } else if (users.indexOf(user) >= 0) {
      setLiked(true);
    }
  }, [users, user]);

  return (
    <svg
      width="50"
      height="45"
      xmlns="http://www.w3.org/2000/svg"
      onClick={handleClick}
    >
      <g>
        <title>Heart</title>
        <path
          className={liked ? 'emptyHeart' : 'fullHeart'}
          d="m24.95551,11.32701c9.87674,-25.35266 48.57414,0 0,32.59628c-48.57414,-32.59628 -9.87674,-57.94894 0,-32.59628z"
        />
      </g>
      <text
        x="50%"
        y="55%"
        textAnchor="middle"
        dy=".3em"
        fill={liked ? '#fff' : '#000'}
      >
        {likes}
      </text>
    </svg>
  );
};
