const timeToString = (num, unit) => {
  return num + (num === 1 ? ` ${unit} ago` : ` ${unit}s ago`);
};

export const dateToString = (date) => {
  date = new Date(date);
  const now = Date.now();
  console.log(date, now);
  const timeDiff = new Date(Date.now() - date);
  console.log(
    `${timeDiff.getTime()} ${timeDiff.getFullYear()} ${timeDiff.getMonth()} ${timeDiff.getDay()} ${timeDiff.getHours()} ${timeDiff.getMinutes()} ${timeDiff.getSeconds()}`
  );

  let time = timeToString(timeDiff.getSeconds(), 'second');

  if (timeDiff.getFullYear() - 1970 > 0) {
    time = timeToString(timeDiff.getFullYear() - 1970, 'year');
  } else if (timeDiff.getMonth() > 0) {
    time = timeToString(timeDiff.getMonth(), 'month');
  } else if (timeDiff.getDay() > 0) {
    time = timeToString(timeDiff.getDay(), 'day');
  } else if (timeDiff.getHours() > 0) {
    time = timeToString(timeDiff.getHours(), 'hour');
  } else if (timeDiff.getMinutes() > 0) {
    time = timeToString(timeDiff.getMinutes(), 'minute');
  }

  return time;
};
