// import { useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';

const Header = () => {
  return (
    <nav>
      <ul>
        <li>
          <Link to="/">
            <i className="fas fa-home"></i>
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export const SignIn = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);

  return (
    <div className="centerScreen">
      <Header />
      <div className="loginContainer">
        <form onSubmit={handleSubmit(onSubmit)}>
          <label htmlFor="username">Userame</label>
          <input
            id="username"
            type="text"
            placeholder="Enter username"
            aria-invalid={errors.username ? 'true' : 'false'}
            {...register('username', {
              required: true,
              minLength: { value: 3, message: '<p>Minimum</p>' },
              maxLength: 10,
              pattern: /^[a-z0-9]+$/i,
            })}
          />

          {/* {errors.username && errors.username.type === 'required' && (
            <p role="alert">Username is required to login</p>
          )}
          {errors.username && errors.username.type === 'minLength' && (
            <p role="alert">Username must be at least 3 characters long</p>
          )}
          {errors.username && errors.username.type === 'maxLength' && (
            <p role="alert">Username must be 10 characters long maximum</p>
          )}
          {errors.username && errors.username.type === 'pattern' && (
            <p role="alert">Username must contain only letters and numbers</p>
          )} */}

          <input type="submit" />
        </form>
        {/* <form>
          <label for="username">Username</label>
          <input
            type="text"
            placeholder="Enter username"
            name="username"
            required
          />

          <label for="pwd">Password</label>
          <input
            type="password"
            placeholder="Enter password"
            name="pwd"
            required
          />

          <button>Login</button>
        </form> */}
        <div className="swapLogin">
          <p>
            Don't have an account? <Link to="/register">Sign up here!</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export const SignUp = () => {
  return (
    <div className="centerScreen">
      <Header />
      <div className="loginContainer">
        <form>
          <label for="username">Username</label>
          <input
            type="text"
            placeholder="Enter username"
            name="username"
            required
          />

          <label for="email">Email</label>
          <input type="text" placeholder="Enter email" name="email" required />

          <label for="pwd">Password</label>
          <input
            type="password"
            placeholder="Enter password"
            name="pwd"
            required
          />

          <button>Login</button>
        </form>
        <div className="swapLogin">
          <p>
            Already have an account? <Link to="/login">Sign in here!</Link>
          </p>
        </div>
      </div>
    </div>
  );
};
