import { useEffect, useReducer, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Header } from './Header';
import { dateToString } from './postDate';

const Reply = ({ reply }) => {
  const { author, body, date } = reply;

  let time = dateToString(date);

  return (
    <div className="reply">
      <h5>{author}</h5>
      <p className="time">{time}</p>
      <p>{body}</p>
    </div>
  );
};

const Replies = ({ replies }) => {
  return (
    <div className="replies">
      {replies.map((reply) => {
        return <Reply key={reply._id} reply={reply} />;
      })}
    </div>
  );
};

const Comment = ({ postId, commentId, comment }) => {
  const { author, body, replies } = comment;

  const [hideCommentBox, toggleHideCommentBox] = useReducer(
    (hideCommentBox) => {
      return !hideCommentBox;
    },
    true
  );

  const handleClick = () => {
    toggleHideCommentBox();
  };

  return (
    <div className="comment">
      <h5>{author}</h5>
      <p>{body}</p>
      <button onClick={handleClick}>Reply</button>
      <div
        className="commentBox"
        style={{ display: hideCommentBox ? 'none' : 'block' }}
      >
        <textarea rows="1" cols="1" name="text"></textarea>
      </div>
      <Replies replies={replies} />
    </div>
  );
};

const Comments = ({ id, comments }) => {
  return (
    <div className="comments">
      {comments.map((comment) => {
        return (
          <Comment
            key={comment._id}
            postId={id}
            commentId={comment._id}
            comment={comment}
          />
        );
      })}
    </div>
  );
};

const Content = ({ id, likes, author, body, title, date }) => {
  return (
    <div className="content">
      <p>{`${likes} Likes`}</p>
      <div className="contentPost">
        <h3>{title}</h3>
        <h5>{author}</h5>
        <p>{body}</p>
      </div>
    </div>
  );
};

export const Post = () => {
  const { id } = useParams();

  const [data, setData] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/be/getPost/${id}`)
      .then((response) => response.json())
      .then(setData);
  }, [id]);

  if (!data) {
    return (
      <>
        <Header />
        <h1>Loading...</h1>
      </>
    );
  } else {
    const { _id, likes, author, body, title, date, comments } = data;
    return (
      <>
        <Header />
        <div className="postContainer">
          <Content
            id={_id}
            likes={likes}
            author={author}
            body={body}
            title={title}
            date={date}
          />
          <div className="commentBox">
            <textarea rows="1" cols="1" name="text"></textarea>
          </div>
          <Comments id={_id} comments={comments} />
        </div>
      </>
    );
  }
};
