import { useEffect, useReducer, useState } from 'react';

export const LikeButton = ({ totalLikes, usersLiked, postId }) => {
  const [likes, setLikes] = useState(totalLikes);
  const [liked, toggleLiked] = useReducer((liked) => !liked, false);
  const [users, setUsers] = useState(usersLiked);
  const user = localStorage.getItem('name');

  const handleClick = () => {
    if (user) {
      if (liked) {
        setLikes(likes - 1);
      } else {
        setLikes(likes + 1);
      }
      toggleLiked();
    } else {
    }
  };

  useEffect(() => {
    if (users.indexOf(user) >= 0) {
      toggleLiked();
    }
  }, [users, user]);

  return (
    <svg width="400" height="400" xmlns="http://www.w3.org/2000/svg">
      <g>
        <title>Heart</title>
        <path
          stroke="#000"
          stroke-width="24"
          id="template"
          d="m199.85716,102.30802c77.30535,-221.70482 380.19024,0 0,285.04906c-380.19024,-285.04906 -77.30535,-506.75388 0,-285.04906z"
          fill="#fff"
        />
      </g>
      <text x="50%" y="50%" text-anchor="middle" dy=".3em">
        {likes}
      </text>
    </svg>
  );
};
