import { useEffect, useState } from 'react';
import Alert from 'react-popup-alert';

export const LikeButton = ({ totalLikes, usersLiked, postId }) => {
  const [likes, setLikes] = useState(totalLikes);
  const [liked, setLiked] = useState(false);
  const [users, setUsers] = useState([]);
  const user = localStorage.getItem('name');

  const handleClick = () => {
    const userLikeInfo = {
      postId,
      username: user,
    };
    const options = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userLikeInfo),
    };

    if (user) {
      if (liked) {
        setLikes(likes - 1);
        setUsers(users.filter((name) => name !== user));
        fetch('http://localhost:3001/be/unlikePost', options).then((response) =>
          response.json()
        );
      } else {
        setLikes(likes + 1);
        setUsers((oldUsers) => [...oldUsers, user]);
        fetch('http://localhost:3001/be/likePost', options).then((response) =>
          response.json()
        );
      }
      setLiked(!liked);
    } else {
      showAlert();
      console.log('not logged in');
    }
  };

  useEffect(() => {
    if (usersLiked) {
      setUsers(usersLiked);
      setLiked(usersLiked.indexOf(user) >= 0);
    }
  }, [usersLiked, user]);

  // alert
  const [alert, setAlert] = useState({
    type: 'error',
    text: 'You need to login to like posts!',
    show: false,
  });

  const showAlert = () => {
    setAlert({
      type: 'error',
      text: 'You need to login to like posts!',
      show: true,
    });
  };

  const hideAlert = () => {
    setAlert({
      type: 'error',
      text: 'You need to login to like posts!',
      show: false,
    });
  };

  return (
    <>
      <div className="postLikes" onClick={handleClick}>
        <svg width="50" height="45" xmlns="http://www.w3.org/2000/svg">
          <g>
            <title>Like</title>
            <path
              className={liked ? 'fullHeart' : 'emptyHeart'}
              d="m24.95551,11.32701c9.87674,-25.35266 48.57414,0 0,32.59628c-48.57414,-32.59628 -9.87674,-57.94894 0,-32.59628z"
            />
          </g>
          <text
            x="50%"
            y="55%"
            textAnchor="middle"
            dy=".3em"
            fill={liked ? '#fff' : '#000'}
          >
            {likes}
          </text>
        </svg>
      </div>
      <Alert
        header={'Header'}
        btnText={'Close'}
        text={alert.text}
        type={alert.type}
        show={alert.show}
        onClosePress={hideAlert}
        pressCloseOnOutsideClick={true}
        showBorderBottom={true}
        alertStyles={{}}
        headerStyles={{}}
        textStyles={{}}
        buttonStyles={{}}
      />
    </>
  );
};
