// import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useEffect, useState } from 'react';

const Header = () => {
  return (
    <nav>
      <ul>
        <li>
          <Link to="/">
            <i className="fas fa-home"></i>
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export const SignIn = () => {
  const userErrors = {
    required: 'Username is required to login',
    minLength: 'Username must be at least 3 characters long',
    maxLength: 'Username must be 10 characters long maximum',
    'No user found!': 'No user found with that username',

    err: 'Error trying to login, please try again later',
  };
  const passErrors = {
    required: 'Password is required to login',
    minLength: 'Password must be at least 6 characters long',
    maxLength: 'Password must be 24 characters long maximum',
    'Incorrect password!': 'Incorrect password',
    err: 'Error trying to login, please try again later',
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const [userError, setUserError] = useState(null);
  const [passError, setPassError] = useState(null);

  const onSubmit = async (data) => {
    setUserError(null);
    setPassError(null);
    const loginInfo = {
      username: data.username,
      pwd: data.password,
    };

    const options = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(loginInfo),
    };
    const res = await fetch('http://localhost:3001/be/userLogin', options).then(
      (response) => response.json()
    );

    if (res.succ) {
      localStorage.setItem('name', res.succ);
    } else {
      if (res.err === 'No user found!') {
        setUserError('No user found!');
      } else if (res.err === 'Incorrect password!') {
        setPassError('Incorrect password!');
      } else {
        setPassError('err');
        setUserError('err');
      }
    }
  };
  const onErrors = (errors) => {
    setUserError(errors.username ? errors.username.type : null);
    setPassError(errors.password ? errors.password.type : null);
  };

  return (
    <div className="centerScreen">
      <Header />
      <div className="loginContainer">
        <form onSubmit={handleSubmit(onSubmit, onErrors)}>
          <label htmlFor="username">Userame</label>
          <input
            id="username"
            type="text"
            placeholder="Enter username"
            aria-invalid={errors.username ? 'true' : 'false'}
            {...register('username', {
              required: true,
              minLength: 3,
              maxLength: 30,
            })}
          />

          {userError && <span role="alert">{userErrors[userError]}</span>}

          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            placeholder="Enter password"
            aria-invalid={errors.password ? 'true' : 'false'}
            {...register('password', {
              required: true,
              minLength: 6,
              maxLength: 24,
            })}
          />

          {passError && <span role="alert">{passErrors[passError]}</span>}

          <input type="submit" value="Login" />
        </form>

        <div className="swapLogin">
          <p>
            Don't have an account? <Link to="/register">Sign up here!</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export const SignUp = () => {
  const userErrors = {
    required: 'Username is required to login',
    minLength: 'Username must be at least 3 characters long',
    maxLength: 'Username must be 10 characters long maximum',
    pattern: 'Username must contain only letters and numbers',
  };
  const emailErrors = {
    required: 'Email is required to login',
    maxLength: 'Email must be 30 characters long maximum',
    pattern: 'Email must be in the correct format',
  };
  const passErrors = {
    required: 'Password is required to login',
    minLength: 'Password must be at least 6 characters long',
    maxLength: 'Password must be 24 characters long maximum',
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const [userError, setUserError] = useState(null);
  const [emailError, setEmailError] = useState(null);
  const [passError, setPassError] = useState(null);

  const onSubmit = async (data) => {
    console.log(data);
  };

  const onErrors = (errors) => {
    setUserError(errors.username ? errors.username.type : null);
    setEmailError(errors.email ? errors.email.type : null);
    setPassError(errors.password ? errors.password.type : null);
  };

  return (
    <div className="centerScreen">
      <Header />
      <div className="loginContainer">
        <form onSubmit={handleSubmit(onSubmit, onErrors)}>
          <label htmlFor="username">Userame</label>
          <input
            id="username"
            type="text"
            placeholder="Enter username"
            aria-invalid={errors.username ? 'true' : 'false'}
            {...register('username', {
              required: true,
              minLength: 3,
              maxLength: 10,
              pattern: /^[a-z0-9]+$/i,
            })}
          />

          {userError && <span role="alert">{userErrors[userError]}</span>}

          <label htmlFor="email">Email</label>
          <input
            id="email"
            type="email"
            placeholder="Enter email"
            aria-invalid={errors.email ? 'true' : 'false'}
            {...register('email', {
              required: true,
              maxLength: 30,
              pattern: /\S+@\S+\.\S+/,
            })}
          />

          {emailError && <span role="alert">{emailErrors[emailError]}</span>}

          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            placeholder="Enter password"
            aria-invalid={errors.password ? 'true' : 'false'}
            {...register('password', {
              required: true,
              minLength: 6,
              maxLength: 24,
            })}
          />

          {passError && <span role="alert">{passErrors[passError]}</span>}

          <input type="submit" value="Login" />
        </form>

        <div className="swapLogin">
          <p>
            Already have an account? <Link to="/login">Sign in here!</Link>
          </p>
        </div>
      </div>
    </div>
  );
};
