import { useEffect, useReducer, useState } from 'react';

export const LikeButton = ({ totalLikes, usersLiked, postId }) => {
  const [likes, setLikes] = useState(totalLikes);
  const [liked, toggleLiked] = useReducer((liked) => !liked, false);
  const [users, setUsers] = useState(usersLiked);
  const user = localStorage.getItem('name');

  const handleClick = () => {
    if (user) {
      if (liked) {
        setLikes(likes - 1);
      } else {
        setLikes(likes + 1);
      }
      // send new likes count to server
      toggleLiked();
    } else {
      // alert user to sign in to like a post
    }
  };

  useEffect(() => {
    if (!users) {
      setUsers([]);
    } else if (users && users.indexOf(user) >= 0) {
      toggleLiked();
    }
  }, [users, user]);

  return (
    <svg width="44" height="39" xmlns="http://www.w3.org/2000/svg">
      <g>
        <title>Layer 1</title>
        <path
          id="svg_5"
          d="m-24.125,-20.77875c0.305,-0.875 1.5,0 0,1.125c-1.5,-1.125 -0.305,-2 0,-1.125z"
          stroke-width="24"
          stroke="#000"
          fill="#fff"
        />
        <path
          stroke="#000"
          id="svg_7"
          d="m22.13795,9.8048c8.71508,-21.61564 42.86104,0 0,27.79154c-42.86104,-27.79154 -8.71508,-49.40718 0,-27.79154z"
          stroke-width="2"
        />
      </g>
      <text x="50%" y="50%" text-anchor="middle" dy=".3em">
        {likes}
      </text>
    </svg>
  );
};
